    //     (1)   (1)   (2)
    // (1) 1+1   1+1   1+2
    // (2) 1+2   1+2   2+2
    // (3) 1+3   1+3   2+3
    // 
    // If k = 2 then the answer must be in the left top 2x2 square

    // Iterator for one column
    private class Iterator implements Comparable<Iterator> {

        private final int[] nums;
        private final int other;
        private int current = 0;

        public Iterator(int[] nums, int other) {
            this.nums = nums;
            this.other = other;
        }

        public boolean hasNext() {
            return current + 1 < nums.length;
        }

        public void next() {
            current++;
        }

        public List<Integer> getPair() {
            final List<Integer> pair = new ArrayList<>();
            pair.add(other);
            pair.add(nums[current]);
            return pair;
        }

        public int getSum() {
            return nums[current] + other;
        }

        public int compareTo(Iterator other) {
            return Integer.compare(this.getSum(), other.getSum());
        }
    }

    public List<List<Integer>> kSmallestPairs(int[] nums1, int[] nums2, int k) {
        final List<List<Integer>> result = new ArrayList<>();

        if (nums1.length == 0 || nums2.length == 0) return result;

        final PriorityQueue<Iterator> queue = new PriorityQueue<>();
        for (int i = 0; i < Math.min(k, nums1.length); i++) {
            queue.offer(new Iterator(nums2, nums1[i]));
        }

        while (result.size() < k && !queue.isEmpty()) {
            final Iterator iterator = queue.poll();
            result.add(iterator.getPair());

            if (iterator.hasNext()) {
                iterator.next();
                queue.offer(iterator);
            }
        }

        return result;
    }
}
