class Solution {
    public List<List<Integer>> kSmallestPairs(int[] nums1, int[] nums2, int k) {
        int n = nums1.length;
        int m = nums2.length;
        PriorityQueue<Pair>queue = new PriorityQueue<>((a,b) -> a.val - b.val);
        HashSet<String>set = new HashSet<>();
        List<List<Integer>>arr = new ArrayList<>();
        queue.add(new Pair(nums1[0] + nums2[0],0,0));
        while(k > 0 && !queue.isEmpty()){
            Pair p = queue.poll();
            int x = p.x;
            int y = p.y;
            arr.add(new ArrayList<>(Arrays.asList(nums1[x],nums2[y])));
            if(x+1 < n && set.add((x+1) + "" + y)){
                queue.add(new Pair(nums1[x+1] + nums2[y],x+1,y));
            }
            if(y+1 < m && set.add((x) + "" + (y+1))){
                queue.add(new Pair(nums1[x] + nums2[y+1],x,y+1));
            }
            k--;
        }
        return arr;
    }
}

