/** 
 * Forward declaration of guess API.
 * @param  num   your guess
 * @return 	     -1 if num is higher than the picked number
 *			      1 if num is lower than the picked number
 *               otherwise return 0
 * int guess(int num);
 */

class Solution {
public:
    int guessNumber(int n) {

        // As the guessed number is between 1 to n
        int low = 1;
        int high = n;

        while(low <= high)
        {

            // find the mid

            int mid = low + (high - low)/2;

            int test = guess(mid);

            // Move the low and high pointer based on the condition.
            if(test == 0)
                return mid;
            else if(test == -1)
                high = mid-1;
            else
                low = mid +1;    
        }
        return -1;
    }
};
```

