int Solution::guessNumber(int n) {
    int left{1}, right{n}, middle=n/2, ret;
    while ((ret=guess(middle)) != 0) { 
        (ret == -1) ? right = middle-1 : left = middle+1;
        middle = left + (right-left)/2;
    }
    return middle;
}
