public class Solution extends GuessGame {
	public int guessNumber(int n) {
		int left = 0;
		int right = n;
		while (left <= right) {
			int c = right - ((right - left) >> 1); // we would never get overflow
			int val = guess(c);
			if (0 == val) return c;
			if (1 == val) left = c + 1;
			else right = c - 1;
		}
		return 0;
	}
}

