class Solution {
public:
    int guessNumber(int n) {
        int high=n, low=1;
        while(low<=high){
            int mid= low+(high-low)/2;
            if(guess(mid)==0){
                return mid;
            }
            else if(guess(mid)<0){
                high= mid-1;
            }
            else{
                low= mid+1;
            }
        }
        return 0;
    }
};
```

