class Solution {
    int total = 0;

    public int combinationSum4(int[] nums, int target) {
        total = 0;
        Arrays.sort(nums);
        backtrack(nums, target);
        return total;
    }

    private void backtrack(int[] nums, int remain) {
        if (remain == 0) {
            total++;
            return;
        }

        for (int i = 0; i < nums.length; i++) {
            if (nums[i] > remain) {
                break;
            }

            backtrack(nums, remain - nums[i]);
        }
    }
}
