class Solution {

    int[] cache;
    public int combinationSum4(int[] nums, int target) {
        Arrays.sort(nums);

        //Use memoisation techniques,
        cache = new int[target + 1];
        Arrays.fill(cache, -1);


        return findCombination(nums, target);
    }

    private int findCombination(int[] nums, int target){
        //base case
        if(target ==  0) return 1;

        //retrieve from cache
        if(cache[target] != -1) return cache[target];


        int count = 0;
        for(int num : nums){
            //remaining target sum is less than 0, 
            //no need to check further, as we already sorted the nums array
            //in increasing order. further Remaining  target will be more -ve
            if(target - num < 0) 
                break;
            //find the  permutation for remaining target sum,
            //and append th count
            count += findCombination(nums, target - num);
        }

        //store in cache
        cache[target] = count;

        return count;
    }
}
