class Solution { // Memoization - Top Down (0ms - 100% Faster)
    Integer[] dp; int target,nums[];
    public int combinationSum4(int[] nums, int target) {
        dp = new Integer[target+1];
        dp[target]=1;
        this.target=target;
        this.nums=nums;
        return recurse(0);
    }
    private int recurse(int done_till){
        if(dp[done_till]!=null)
            return dp[done_till];
        int res=0;
        for(int val : nums)
            if((done_till+val)<=target)
                res+=recurse(done_till+val);
        return dp[done_till]=res;
    }
}
```
