class Solution { // Tabulation - Bottom Up (1ms - 93% Faster)
    public int combinationSum4(int[] nums, int target) {
        int i,dp[] = new int[target+1];
        for(i=dp[0]=1;i<=target;i++)
            for(int val : nums)
                if((i-val)>=0) 
					dp[i]+=dp[i-val];
        return dp[target];
    }
}
