var combinationSum4 = function(nums, target, memo={}) {
  if (target ==0) return 1; 
  if (typeof memo[target] != "undefined") return memo[target];
   let ret = 0;

    for (let i = 0; i<nums.length; i++) {
        if (target >= nums[i])
            ret += combinationSum4(nums, target - nums[i], memo);
    }
    memo[target] = ret;
    return ret;
};
```

