class Solution {
public:
    vector<vector<int>>dp;
    int recur(vector<int>&nums,int idx,int target){
        if(target == 0){
            return 1;
        }
        if(idx == -1){
            return 0;
        }

        if(dp[idx][target] != -1){
            return dp[idx][target];
        }

        // Left the element at idx
        int ans = recur(nums,idx-1,target);

        // we took the element at idx & recur again
        // for all the numbers again, thus passing the idx of last element
        if(target >= nums[idx]){
            ans += recur(nums,nums.size()-1,target-nums[idx]);
        }

        return dp[idx][target] = ans;
    }

    int combinationSum4(vector<int>& nums, int target) {

        int n = nums.size();
        dp.resize(n,vector<int>(target+1,-1));

        return recur(nums,n-1,target);
    }
};
