        vector<unsigned long long> ways(target+1, 0); 
        ways[0] = 1;
        for(int i = 1; i < ways.size(); i++){
            for(int j = 0; j < nums.size(); j++){
                int val = nums[j];
                if(val <= i)
                    ways[i] += ways[i-val];
            }
        }
        return (int)ways[target]; //The Answer
