```
//             4
//         1. 2   3
//     ->. 3. 2.  1 
//      1 2 3 
// ->   2 1 0                 
// https://www.youtube.com/watch?v=PomVtsn7ZoM

class Solution {
    public int combinationSum4(int[] nums, int target) {

        if (target == 0) {
            return 0;
        }
        int[] dp = new int[target + 1];

        // One possible way to make sum as 0 by not selecting any
        dp[0] = 1;

        // dp for all targets of sum
        for (int i = 1; i <= target; i++) {

            // all given numbers process the state space tree
            for (int j = 0; j < nums.length; j++) { 

                // only if its lesser than sum
                if (nums[j] <= i) {

                    // current value + remaining target sum combinations
                    dp[i] = dp[i] + dp[i - nums[j]]; // i-nums[j] gives new target eg (4-1) = 3
                }
            }
        }

        return dp[target];
    }
}
```

