class Solution {
    public int combinationSum4(int[] nums, int target) {
        int n = nums.length;
        Arrays.sort(nums);
        int[] dp = new int[target+1];
        HashSet<Integer> set = new HashSet<>();
        for(int i: nums){
            set.add(i);
