        sep = { "12" : "1|2", "21" : "2|1", "13" : "1|3", 
                "31" : "3|1", "23" : "2|3", "32" : "3|2"}

        say = "1"

        # [2] at each iteration:
        #     - first, place separators between blocks
        #     - second, split using separators and build new saying
        for _ in range(n-1):
