class Solution {
    public String countAndSay(int n) {
        if(n==1){
            return "1";

        }
        if(n==2){
            return "11";
        }
       String s="11";
        for(int i=3;i<=n;i++){
            String t="";
            s=s+'$';
            int c=1;
            for(int j=1;j<n;j++){
                if(s[j-1]==s[j]){
                    c++;
                }
                else{
                    t=t+to_string(c);
                    t=t+s[j-1];
                    c=1;
                }
            }
            s=t;


        }
        return s;
    }
};






