 var deleteDuplicates = function(head) {

    let current = head;

    while (current) {
        let runner = current;

        // remove dups after the current node 
        while (runner.next) {
            if (runner.val === runner.next.val) {
                // skip Dups
                runner.next = runner.next.next;
            } else {
                runner = runner.next;
            }
        }
        current = current.next;   
    }

    return head;
}
```


