var deleteDuplicates = function(head) {
    const dups = {};
    let cur = head;
    let prev;
    while (cur) {
        if (!dups[cur.val]) {
            dups[cur.val] = true;
            prev = cur;
        } else {
            prev.next = cur.next;
        }
        cur = cur.next;
    }

    return head;
};
```

