class Solution:
    def firstUniqChar(self, s: str) -> int:
        best = float('inf')
        for i in range(26):
            c = chr(i + ord('a'))
            l = s.find(c)
            r = s.rfind(c)
            if l != -1 and l == r:
                best = min(best, l)
        return best if best != float('inf') else -1
