class Solution {
    public int firstUniqChar(String s) {
        Queue<Character> ch = new LinkedList<Character>();
        int len = s.length();
        char temper = 0;
        for(int j = 0; j < s.length(); j++)
        {
            ch.offer(s.charAt(j));
        }
        while(len > 0)
        {
            temper = ch.poll();
            if(!ch.contains(temper))
            {
                return s.indexOf(temper);
            }
            else
            {
                ch.offer(temper);
            }
            len--;
        }
        return -1;
    }
}

