```
typedef struct {
    int count;
    int pos;
} id;

class Solution {
public:
    int firstUniqChar(string s);
};
/******************************************************************/
int Solution::firstUniqChar(string s) {
    unordered_map<char,id> m;
    unordered_map<char,id>::iterator it;
    int i, size = s.size(), firstIndex= INT_MAX;
    for (i = 0; i < size; ++i) {
        ++m[s[i]].count;
        if (m[s[i]].count == 1) {
            m[s[i]].pos = i;
        }
    }
    for (it = m.begin(); it != m.end(); ++it) {
        if (it->second.count == 1 && m[it->first].pos < firstIndex) {
            firstIndex = m[it->first].pos;
        }
    }
    if (firstIndex == INT_MAX) {
        firstIndex = -1;
    }
    return firstIndex;
}
/******************************************************************/
```

