def combSum(A, x):
    if A == [] or x < A[0]:
        return
    elif x == A[0]:
        yield [x]
    else:
        for comb in combSum(A, x - A[0]):
            yield [A[0]] + comb
        for comb in combSum(A[1:], x):
            yield comb

