class Solution {
public:
    vector<vector<int>> ans;
    vector<int> val;
    int n;

    void solve(vector<int> &arr, int id, int target) {
        if(target < 0) return;
        if(target == 0) {
            ans.push_back(arr);
            return;
        }
        for(int i = id; i < n; i++) {
            arr.push_back(val[i]);
            solve(arr, i, target - val[i]);
            arr.pop_back();
        }
    }

    vector<vector<int>> combinationSum(vector<int>& candidates, int target) {
        n = candidates.size();
        val = candidates;
        vector<int> arr;
        solve(arr, 0, target);
        return ans;
    }
};
