    	//pick the element
        //if current element is less or equal to target then do the combination
    	if(target >= currentVal) {
    		currentAns.push_back(currentVal);
    		solve(candidates , target-currentVal , idx , currentAns ,answer);
    		 //make sure u remove the element while recursion is over
    		currentAns.pop_back();
    	}

    	//not pick up element
    	solve(candidates , target , idx+1 , currentAns , answer);

    }

};
