class Solution {
    vector<vector<int>> res;
public:
    vector<vector<int>> combinationSum(vector<int>& candidates, int target) {

        vector<int> temp;
        vector<vector<int>> ans;
        solve(candidates, target, 0, temp, 0);

        return res;
    }

    void solve(vector<int>& candidates, int target, int currSum, vector<int>& temp, int pos)
    {
        if(currSum > target)
            return;
        if(currSum == target)
        {
            res.push_back(temp);
            return;
        }

        for(int i=pos; i<candidates.size(); i++)
        {
            currSum += candidates[i];
            temp.push_back(candidates[i]);
            solve(candidates, target, currSum, temp, i);
            currSum -= candidates[i];
            temp.pop_back();
        }
    }
};

