        if (target == 0) {
            ans.add(new ArrayList<>(tempList));
        }
        if (target < 0) return;

        for (int i = start; i < nums.length && target - nums[i] >= 0; ++i) {
            tempList.add(nums[i]);
            recurse(nums, target - nums[i], i, tempList, ans);
            tempList.remove(tempList.size() - 1);
        }
    }
}
