class Solution {
public:
    vector<vector<int>> combinationSum(vector<int>& candidates, int target) {
        vector<vector<int>> res;
        vector<int> tmp;
        helper(candidates, target, 0, res, tmp);
        return res;
    }
    void helper(vector<int>& candidates, int target, int start, vector<vector<int>>& res, vector<int>& tmp){
        for (int i = start; i < candidates.size(); ++i){
            int diff = target - candidates[i];
            if (diff == 0){
                tmp.push_back(candidates[i]);
                res.push_back(tmp);
                tmp.pop_back();
            }
            else if (diff > 0){
                tmp.push_back(candidates[i]);
                helper(candidates, diff, i, res, tmp);
                tmp.pop_back();
            }
        }
    }
};
