```
ListNode* reverseBetween(ListNode* head, int m, int n) 
{
	auto *prev{head}, *h{head}, **start{&head};

	for(; prev = exchange(h, h->next), --m && --n; start = &prev->next); 
	for(; --n; prev = exchange(h, exchange(h->next, prev))); 

	(*start)->next = h, *start = prev;

	return head;
}

