#include <iostream>

struct ListNode {
	int val;
	ListNode *next;
	ListNode() : val(0), next(nullptr) {}
	ListNode(int x) : val(x), next(nullptr) {}
	ListNode(int x, ListNode *next) : val(x), next(next) {}
};

class Solution {
public:
	ListNode* reverseBetween(ListNode* head, int left, int right) {
		if (!head || !head->next || left == right) 
			return head;

		ListNode dummy(0), *prev = &dummy, *curr = head;
		dummy.next = head;
		for (int i = 1; i < left; i++) {
			prev = curr;
			curr = curr->next;
		}

		ListNode *tail = curr, *prev2 = nullptr;
		for (int i = left; i <= right; i++) {
			ListNode *next = curr->next;
			curr->next = prev2;
			prev2 = curr;
			curr = next;
		}

		prev->next = prev2;
		tail->next = curr;
		return dummy.next;
	}
};

void main() {
	//Input: head = [1,2,3,4,5], left = 2, right = 4
	ListNode *head = new ListNode(1);
	head->next = new ListNode(2);
	head->next->next = new ListNode(3);
	head->next->next->next = new ListNode(4);
	head->next->next->next->next = new ListNode(5);
	int left = 2, right = 4;

	Solution oSolution;
	ListNode *res = oSolution.reverseBetween(head, left, right);

	std::cout << "[ " ;
	while (res) {
		std::cout << res->val << " ";
		res = res->next;
	}
	std::cout << "]";
	std::cout << std::endl;

	// Input: head = [5], left = 1, right = 1
	head = new ListNode(5);
	left = 1, right = 4;
	res = oSolution.reverseBetween(head, left, right);

	std::cout << "[ ";
	while (res) {
		std::cout << res->val << " ";
		res = res->next;
	}
	std::cout << "]";
	std::cout << std::endl;

	system("pause");
}
/**
Output:
[ 1 4 3 2 5 ]
[ 5 ]
**/
