        ListNode newCurrent = current;
        ListNode prev = null;

        while(current != null && m <= n){
            ListNode temp = current.next;
            current.next = prev;
            prev = current;
            current = temp;
            m++;
        }

        if(previous == null){
            head = prev;
        } else {
            previous.next = prev;
        }

        newCurrent.next = current;
        return head;
    }
