class Solution {
public:
    ListNode* reverseBetween(ListNode* head, int left, int right) {
        if(left == right || !head || !head->next)return head;

        ListNode* temp = head;
        vector<int> order;

        while(temp != NULL)
        {
            order.push_back(temp->val);
            temp=temp->next;
        }

        reverse(order.begin() + left - 1 , order.begin() + right);
        int index = 0;
        temp = head;

        while(temp != NULL)
        {
            temp->val = order[index++];
            temp = temp->next;
        }

        return head;

    }
};
