class Solution {
public:
    ListNode* reverseBetween(ListNode* head, int left, int right) {
        ListNode * start = head;
        ListNode * tempPrev = new ListNode(0,head); // Will be at left - 1
        int cnt = 1;
        while(cnt < left)
            cnt++,tempPrev = tempPrev->next,head=head->next;
        ListNode * temp = head;
        ListNode * prev_p = NULL;
        ListNode * curr_p = head;
        ListNode * next_p;

