            next_p = curr_p -> next;
            curr_p -> next = prev_p;

            prev_p = curr_p;
            curr_p = next_p;
            cnt++;
        }
        head = prev_p;
        tempPrev->next = head;
        temp->next = curr_p;
        if(left == 1) // Starting point will change
            return head;
        return start;
    }
