class Solution {
public:
    ListNode* F(ListNode* curr) {
        ListNode *prev = nullptr, *next = curr;
        while(curr) {
            next = next -> next;
            curr -> next = prev;
            prev = curr;
            curr = next;
        }
        return prev;
