class Solution {
private:
    // Iterative Approach
    ListNode* reverse(ListNode* head){

        ListNode* prev = NULL;
        ListNode* curr = head;
        ListNode* next = NULL;

        while(curr != NULL){
            next = curr -> next;
            curr -> next = prev;
            prev = curr;
            curr = next;
        }

        return prev;
    }
public:
    ListNode* reverseBetween(ListNode* head, int left, int right) {

        ListNode* prev = NULL;
        ListNode* curr = head;

        int count = 1;
        while(count != left){
            prev = curr;          // prev will be placed just before left
            curr = curr -> next;
            count++;
        }

        ListNode* start = curr;   // start is placed on left 

        while(count != right){
            curr = curr -> next;
            count++;
        }

        ListNode* end = curr;       // end is placed on right
        curr = curr -> next;        // curr is placed just after right
        end -> next = NULL;        

        ListNode* newHead = reverse(start);    // reversing Linked List from start to end (NULL)

        if(prev != NULL)
            prev -> next = newHead;        // as prev stores left-side data not reversed
        else
            head = newHead;               // when (left == 1) -> prev is still at NULL

        while(newHead -> next != NULL)
            newHead = newHead -> next;

        newHead -> next = curr;        // as curr stores right-side data not reversed

        return head;

    }
};
```

