class Solution(object):
    def reverseBetween(self, head, left, right):
        if left >= right:
            return head

        ohead = newhd = ListNode(0)
        whead = wlast = head
        newhd.next = head
        for i in range(right-left):
            wlast = wlast.next
        for i in range(left-1):
            ohead, whead, wlast = whead, whead.next, wlast.next 
        olast, wlast.next = wlast.next, None
        revhead, revlast = self.reverse(whead)
        ohead.next, revlast.next = revhead, olast
        return newhd.next

    def reverse(self, head):
        pre, curr, last = None, head, head
        while curr:
            curr.next, pre, curr = pre, curr, curr.next
        return pre, last
