        f[2] = True
        f[3] = True

        for num in xrange(4, n + 1):
            this_turn_can_win = False
            for k in xrange(1, 4):
                if not f[num - k]:
                    this_turn_can_win = True
                    break

            f[num] = this_turn_can_win

        return f[n]
