class Solution {
    public boolean canWinNim(int n) {
        // for each of the person has choice to take at most 3 stones
        // each time a person can pick 1 stone , 2 stone, or 3 stone
        // so for n = 1,2,3 the first person wins
        // for 4, the first person looses
        // so its evident that first person wins if the second person leaves behind 1 or 2 or 3 stones.

		//recursive - TLE
        if (n <= 0) return false;
        if (n == 1 || n == 2 || n == 3) return true;
        if (canWinNim(n-1) && canWinNim(n-2) && canWinNim(n-3)) return false;
        return true;

		//modulo - math
		return n % 4 != 0;
    }
}
