        if (s[m - 1] == t[n - 1])
            return isSubs(s, t, m - 1, n - 1);

        // If last characters are
        // not matching
        return isSubs(s, t, m, n - 1);
    }


    bool isSubsequence(string s, string t) {

       if( isSubs(s,t,s.length(),t.length()))
           return true;

        return false;

