The follow-up question is the leetcode problem 792 at https://leetcode.com/problems/number-of-matching-subsequences/description/

```
class Solution {
public:
    int numMatchingSubseq(string S, vector<string>& words) {
        int size = S.size();
        charToIdx.resize(26);
        for(int i=0; i<size; i++) charToIdx[S[i]-'a'].push_back(i);
        int ret = 0; for(auto& word : words) {if(isSubsequence(word)) ret += 1;}
        return ret;
    }
private:
    vector<vector<int>> charToIdx;
    bool isSubsequence(string& word) {
        int wsize = word.size();
        int idx = -1;
        for(int i = 0; i<wsize; i++) {
            char c = word[i];
            int offset = c-'a';
            auto& range = charToIdx[offset];
            if(range.empty()) return false;
            //binary search for the first index in the range that is more the running 'idx'
            int left = 0; int right = (int)range.size() - 1; int mid; int result = -1;
            while(left <= right) {
                mid = left + (right-left)/2;
                if(range[mid]>idx) {result = range[mid]; right = mid-1;}
                else {left = mid+1;}
            }
            if(result == -1) return false;
            else idx = result;
        }
        return true;
    }
};
```

