class Solution {
public:
    bool isSubsequence(string s, string t) {
        int n1= s.size();
        int t1 = t.size();
        if(s.empty())return true;
        if(n1>t1)return false;
        for(int i = 0; i < t1;i++)
        {
            if(s[0]==t[i])s.erase(s.begin());
        }
        return s.empty();
    }
};
