**Main idea is to find Longest Common Subsequence of S  In String T**
```
class Solution {
public:

    int LCS(string s,string t,int n,int m)
    {
        int dp[n+1][m+1];

        for(int i=0;i<=n;i++)
        {
            for(int j=0;j<=m;j++)
            {
                if((i==0)||(j==0))
                {
                    dp[i][j]=0;
                }
            }
        }



        for(int i=1;i<=n;i++)
        {
            for(int j=1;j<=m;j++)
            {
                if(s[i-1]==t[j-1])
                {
                   dp[i][j]=1+dp[i-1][j-1]; 
                }
                else
                {
                    dp[i][j]=max(dp[i-1][j],dp[i][j-1]);
                }
            }
        }

        return dp[n][m];


    }

    bool isSubsequence(string s, string t)
    {
        int  n=s.size();
        int m=t.size();

        int x=LCS(s,t,n,m);

        if(x==s.size())
        {
            return true;
        }

        return false;
    }
};
```

