class Solution {
public:
    bool isSubsequence(string s, string t) {
        const char* ps = s.c_str();
        const char* pt = t.c_str();

        while(*ps != '\0' && *pt != '\0') {
            if(*ps == *pt) {
                ++ps;
            }
            ++pt;
        }

        return *ps == '\0';
    }
