    class Solution {
    public:
        vector<int> inorderTraversal(TreeNode *root) {
            vector<int> vector;
            if(!root)
            return vector;
            stack<TreeNode *> stack;
            stack.push(root);
            while(!stack.empty())
            {
                TreeNode *pNode = stack.top();
                if(pNode->left)
                {
                    stack.push(pNode->left);
                    pNode->left = NULL;
