    int* inorderTraversal(struct TreeNode* root, int* returnSize) {
    int *result = NULL;
    *returnSize = 0;

    struct TreeNode **stack = (struct TreeNode **)malloc(sizeof(struct TreeNode *));
    struct TreeNode *pop;
    int length = 0, state = 1;
