class Solution {
    func inorderTraversal(_ root: TreeNode?) -> [Int] {
        var result = [Int](), array = [TreeNode](), head = root
        while head != nil || !(array.isEmpty) {
            switch head {
            case .some(let node):
                array.append(node)
                head = node.left
            default:
                let prev = array.removeLast()
                result.append(prev.val)
                head = prev.right
            }
        }
        return result
    }
}
