vector<int> inorderTraversal(TreeNode* root) {
        vector<int> ans;
        TreeNode* node = root;
        stack<TreeNode*> st;
        while(true)
        {
            if(node != NULL)
            {
                st.push(node);
                node = node->left;
            }
            else
            {
                if(st.empty() == true) break;
                else
                {
                    node = st.top();
                    st.pop();
                    ans.push_back(node->val);
                    node = node->right;
                }
            }
        }
        return ans;
    }
