        # Morris (Destroys and recovers the tree)
        def inorder(root: TreeNode | None) -> Iterator[TreeNode]:
            node = root
            while node:
                if node.left:
                    last = node.left
                    while last.right and last.right != node:
                        last = last.right

                    if last.right:
                        last.right = None
                        yield node
                        node = node.right
                    else:
                        last.right = node
                        node = node.left
                else:
                    yield node
                    node = node.right

        return [node.val for node in inorder(root_)]


