            if(isdigit(s[i])){
                int num = 0;
                while(i < s.size() && isdigit(s[i])){
                    num = num * 10 + (s[i++] - '0');
                }
                i--;
                st.push(to_string(num));
            }else if(isalpha(s[i])){
                string word = "";
                while(i < s.size() && isalpha(s[i])){
                    word = word + s[i++];
